// Parse AI response
// Pegar conversationId do primeiro item (do webhook)
const firstItem = $('Webhook: Solicitar Sugestões').first().json;
const conversationId = firstItem.body?.conversationId || firstItem.conversationId || 'unknown';

try {
  const aiResponse = $input.first().json;

  // Tentar pegar o conteúdo de diferentes estruturas possíveis
  let content = '';
  if (aiResponse.message?.content) {
    content = aiResponse.message.content;
  } else if (aiResponse.choices?.[0]?.message?.content) {
    content = aiResponse.choices[0].message.content;
  } else if (aiResponse.text) {
    content = aiResponse.text;
  } else if (typeof aiResponse === 'string') {
    content = aiResponse;
  }

  // Remove markdown code blocks se presentes
  content = content.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

  // Parse JSON
  const parsed = JSON.parse(content);

  return {
    json: {
      conversationId: conversationId,
      suggestions: parsed.suggestions || []
    }
  };
} catch (error) {
  // Se falhar, retornar sugestões padrão
  return {
    json: {
      conversationId: conversationId,
      suggestions: [
        {
          text: "Obrigado por compartilhar essas informações comigo. Vou analisar e preparar uma orientação personalizada para você.",
          tone: "professional"
        },
        {
          text: "Que bom que você está se dedicando! Vamos trabalhar juntos para alcançar seus objetivos de forma saudável.",
          tone: "motivational"
        },
        {
          text: "Entendo sua situação. Vou considerar todos esses pontos na elaboração do seu plano alimentar.",
          tone: "friendly"
        }
      ],
      error: error.message
    }
  };
}
