// Parse resposta da IA
try {
  const aiResponse = $input.first().json;
  let content = '';

  if (aiResponse.message?.content) {
    content = aiResponse.message.content;
  } else if (aiResponse.choices?.[0]?.message?.content) {
    content = aiResponse.choices[0].message.content;
  } else if (typeof aiResponse === 'string') {
    content = aiResponse;
  }

  // Limpar markdown
  content = content.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

  const inbodyData = JSON.parse(content);

  return {
    json: {
      patientId: items[0].json.patientId,
      weight: inbodyData.weight,
      height: inbodyData.height,
      bodyFat: inbodyData.bodyFat,
      leanMass: inbodyData.leanMass,
      fatMass: inbodyData.fatMass,
      bodyWater: inbodyData.bodyWater,
      bmi: inbodyData.bmi,
      visceralFat: inbodyData.visceralFat,
      basalMetabolicRate: inbodyData.basalMetabolicRate,
      measurements: inbodyData.measurements || {},
      muscleDistribution: inbodyData.muscleDistribution || {},
      date: inbodyData.date,
      notes: inbodyData.notes || '',
      transcriptionStatus: 'completed'
    }
  };
} catch (error) {
  console.error('Erro ao parsear InBody:', error);
  return {
    json: {
      patientId: items[0].json.patientId,
      transcriptionStatus: 'error',
      error: error.message
    }
  };
}
