// Parser compatÃ­vel com ambientes legados do Code Node
var items = $input.all();
var firstItem = (items[0] && items[0].json) ? items[0].json : {};
var content = '';

if (firstItem.response) {
  content = firstItem.response;
} else if (firstItem.message && firstItem.message.content) {
  content = firstItem.message.content;
} else if (
  firstItem.choices &&
  firstItem.choices[0] &&
  firstItem.choices[0].message &&
  firstItem.choices[0].message.content
) {
  content = firstItem.choices[0].message.content;
} else if (firstItem.output) {
  content = firstItem.output;
} else if (typeof firstItem === 'string') {
  content = firstItem;
}

content = String(content || '')
  .replace(/```json\n?/gi, '')
  .replace(/```\n?/g, '')
  .replace(/^[\s\uFEFF\xA0]+|[\s\uFEFF\xA0]+$/g, '')
  .trim();

var patientId = 'unknown';

// 1) Tenta pegar do input atual (pÃ³s-Set)
if (items.length > 0 && items[0].json && items[0].json.patientId) {
  patientId = items[0].json.patientId;
}

// 2) Tenta direto do node Webhook
if (patientId === 'unknown') {
  try {
    patientId = $node["Webhook Recebe PDF"].json["patientId"];
  } catch (err) {}
}

// 3) Fallback: varredura completa
if (patientId === 'unknown') {
  for (var i = 0; i < items.length; i++) {
    if (items[i].json && items[i].json.patientId) {
      patientId = items[i].json.patientId;
      break;
    }
  }
}

var dietData;
try {
  dietData = JSON.parse(content);
} catch (jsonError) {
  return [{
    json: {
      patientId: patientId,
      success: false,
      transcriptionStatus: 'error',
      error: 'Resposta da IA nÃ£o Ã© JSON vÃ¡lido: ' + jsonError.message,
      errorDetails: {
        stack: jsonError.stack,
        receivedContent: content.substring(0, 500)
      },
      diet: null
    }
  }];
}

var refeicoes = Array.isArray(dietData.refeicoes) ? dietData.refeicoes : [];
var totalAlimentos = 0;
for (var r = 0; r < refeicoes.length; r++) {
  var alimentos = Array.isArray(refeicoes[r].alimentos) ? refeicoes[r].alimentos : [];
  totalAlimentos += alimentos.length;
}

var resposta = {
  patientId: patientId,
  success: true,
  transcriptionStatus: 'completed',
  transcribedAt: new Date().toISOString(),
  diet: dietData,
  meta: dietData.meta || {},
  macronutrientes: dietData.macronutrientes || {},
  refeicoes: refeicoes,
  micronutrientes: dietData.micronutrientes || [],
  observacoes: dietData.observacoes || [],
  resumo: {
    totalCalorias: dietData.meta && dietData.meta.caloriasDiarias ? dietData.meta.caloriasDiarias : 0,
    totalRefeicoes: refeicoes.length,
    totalAlimentos: totalAlimentos,
    objetivo: (dietData.meta && dietData.meta.objetivo) ? dietData.meta.objetivo : 'nÃ£o especificado'
  }
};

return [{ json: resposta }];
